function handleInputForUppercaseInputs(){document.querySelectorAll('[data-input="uppercase"]').forEach(e=>{e.dataset.inputEvent||(e.setAttribute("data-input-event","true"),e.addEventListener("input",function(){var e=event.target;e.value=e.value.toUpperCase()}))})}function handleInputForSentenccaseInputs(){document.querySelectorAll('[data-input="sentence-case"]').forEach(e=>{e.dataset.inputEvent||(e.setAttribute("data-input-event","true"),e.addEventListener("input",function(){var e=event.target;e.value=capitalizeAll(e.value)}))})}handleInputForUppercaseInputs(),handleInputForSentenccaseInputs();